import { Directive, OnDestroy } from '@angular/core';

import { ReplaySubject } from 'rxjs';

@Directive()
export abstract class Destroyable implements OnDestroy {
  protected destroyed$: ReplaySubject<boolean>;

  constructor() {
    this.destroyed$ = new ReplaySubject(1);
  }

  public ngOnDestroy(): void {
    this.destroyed$.next(true);
    this.destroyed$.complete();
  }
}

import { Directive, HostListener, Input } from '@angular/core';

import { DropdownMenuComponent } from './dropdown-menu.component';

@Directive({
  selector: '[uicDropdownMenuRef]',
  standalone: true,
})
export class DropdownMenuRefDirective {
  @Input() public uicDropdownMenuRef: DropdownMenuComponent;

  @HostListener('click', ['$event'])
  public onClick(event: PointerEvent) {
    this.uicDropdownMenuRef.openMenu(event);
  }
}

import { Directive, Input, OnInit, TemplateRef, ViewContainerRef } from '@angular/core';

@Directive({
  selector: '[uicLet]',
  standalone: true,
})
export class LetDirective implements OnInit {

  @Input() public uicLet: unknown;

  constructor(private viewContainerRef: ViewContainerRef, private templateRef: TemplateRef<{ uicLet: unknown }>) {}

  public ngOnInit(): void {
    this.viewContainerRef.clear();
    this.viewContainerRef.createEmbeddedView(this.templateRef, {
      uicLet: this.uicLet,
    });
  }
}

