@use "common";

$columns: 12;

@mixin col-media-size($wide) {
  @for $i from 1 through $columns {
    &-#{$wide}-#{$i} {
      width: calc($i / $columns * 100%);
    }
  }
}

:host.uic-col {
  display: flex;
